BusinessLogic.Code:
var newError;

if(!Utils.isDefined(me.getStartDate()) || !Utils.isDefined(me.getEndDate()) ||
   Utils.isEmptyString(me.getStartDate()) || Utils.isEmptyString(me.getEndDate())){
  newError = {
    "level": "error",
    "objectClass": "BoWizardTimeFrameSelection",
    "messageID": "CasWizardTimeFrameSelectionEmpty"
  };
  messageCollector.add(newError);
}
else{
  if (Utils.convertAnsiDate2Date(me.getStartDate()) > Utils.convertAnsiDate2Date(me.getEndDate())){
    newError = {
      "level": "error",
      "objectClass": "BoWizardTimeFrameSelection",
      "messageID": "CasWizardTimeFrameSelectionNegativeFrame"
    };
    messageCollector.add(newError);
  }
}
