BusinessLogic.Code:
var liAtmAttachmentList = me.getItemsByParamArray([{"attachmentPKey" : pKey, "op" : "EQ"},
                                                   {"objectStatus" : STATE.DIRTY | STATE.DELETED, "op" : "NCB"}]);
var liAtmAttachment = liAtmAttachmentList[0];
if(Utils.isDefined(liAtmAttachment)){
  liAtmAttachment.delete();
  me.setFilter("objectStatus", STATE.DIRTY | STATE.DELETED, "NCB");
}
