BusinessLogic.Code:
var operationResult = false;
switch (operator) {
  case "gt":
    operationResult = paramToEvaluate > paramBaseToEvaluate;
    break;
  case "ge":
    operationResult = paramToEvaluate >= paramBaseToEvaluate;
    break;
  case "PER":  
    operationResult = paramToEvaluate >= paramBaseToEvaluate;
    break; 
  case "eq":
    operationResult = paramToEvaluate == paramBaseToEvaluate;
    break; 
  case "ne":   
    operationResult = paramToEvaluate != paramBaseToEvaluate;
    break;
  case "le":
    operationResult = paramToEvaluate <= paramBaseToEvaluate;
    break;
  case "lt":
    operationResult = paramToEvaluate < paramBaseToEvaluate;
}
