BusinessLogic.Code:
var promise;
if (bReload == "1" || !Utils.isDefined(me.getLoAttachment())) {
  promise = BoFactory.loadListAsync(LO_BPAATTACHMENT, me.getQueryBy("referencePKey", me.getPKey())).then(
    function (loAttachment) {
      //set opening time list and reset object status for save handling 
      //(make not BoCustomer DIRTY after reloading data via tab click)
      var objectState = me.getObjectStatus();
      me.setLoAttachment(loAttachment);
      me.setObjectStatus(objectState);
    });
}
else {
  promise = when.resolve();
}
