BusinessLogic.Code:
var promise;
if (bReload == "1" || !Utils.isDefined(me.getLoBpaOpenItem())) {
  promise = BoFactory.loadListAsync(LO_BPAOPENITEM, me.getQueryBy("customerPKey", me.getPKey())).then(
    function (loBpaOpenItemJson) {
      //set customer open items list and reset object status for save handling 
      //(make not BoCustomer DIRTY after reloading data via tab click)
      var objectState = me.getObjectStatus();
      me.setLoBpaOpenItem(loBpaOpenItemJson);
      //Calculate OpenItems Total
      me.calculateOpenItemTotal();
      me.setObjectStatus(objectState);
    });
}
else {
  promise = when.resolve();
}
