BusinessLogic.Code:
var promise;
if(bReload == "1" || !Utils.isDefined(me.getLoContactPartner())) {
  promise = BoFactory.loadListAsync(LO_CONTACTPARTNER, me.getQueryBy("customerPKey", me.getPKey())).then(
    function (loContactPartnerJson) {
      loContactPartnerJson.orderBy({"lastName":"ASC"});
      //set contact partner list and reset object status for save handling 
      //(make not BoCustomer DIRTY after reloading data via tab click)
      var objectState = me.getObjectStatus();
      me.setLoContactPartner(loContactPartnerJson);
      me.setObjectStatus(objectState);
      me.getLoContactPartner().getToggleText();
    });
}
else{
  promise = when.resolve();
}
