BusinessLogic.Code:
var promise;

if(bReload == "1" || !Utils.isDefined(me.getLoListingClassification())) {
  promise = BoFactory.loadListAsync(LO_LISTINGCLASSIFICATION, me.getQueryBy("bpaMainPKey", me.getPKey())).then(
    function (loListingClassificationJson) {
      var objectState = me.getObjectStatus();
      me.setLoListingClassification(loListingClassificationJson);
      me.setObjectStatus(objectState);
    });
}
else {
  promise = when.resolve();
}
