BusinessLogic.Code:
var me = this;
var newError;

if(Utils.isDefined(me.getBoDeliveryRole())){
  var deliveryRole = me.getBoDeliveryRole();

  if((deliveryRole.getSdoSplittingOption() === "Yes" || deliveryRole.getSdoSplittingOption() === "Optional") && Utils.isEmptyString(deliveryRole.getSdoSplittingRule())){
    newError = {
      "level": "error", 
      "objectClass": "BoCustomer", 
      "messageID": "CasBpaSplittingRuleHasToBeFilled"
    };
    messageCollector.add(newError);
  }
}
