BusinessLogic.Code:
var acl = me.getACL();
var splittingOption;

if(Utils.isDefined(handlerParams)) {
  splittingOption = handlerParams.newValue;
}
else {
  splittingOption = me.getSdoSplittingOption();
}

if(splittingOption == "No" || splittingOption === "FromSalesDoc") {
  me.setSdoSplittingRule(' ');
  acl.removeRight(AclObjectType.PROPERTY, "sdoSplittingRule", AclPermission.EDIT);
}
else {
  acl.addRight(AclObjectType.PROPERTY, "sdoSplittingRule", AclPermission.EDIT);
}
BindingUtils.refreshEARights();
