// Variables
var menuItem = {};

if (Utils.isDefined(id) && Utils.isDefined(actionImg) && Utils.isDefined(actionId) && Utils.isDefined(processEvent) && Utils.isDefined(actionEnabled))
{		
  menuItem.id = id;
  menuItem.actionImg = actionImg;
  menuItem.actionId = actionId;
  menuItem.processEvent = processEvent;	
  menuItem.actionEnabled= actionEnabled;				
}
