// Create contact partner relations (BpaEmploysRel)
var jsonBpaEmploysRelData = {};

jsonBpaEmploysRelData.pKey = PKey.next();
jsonBpaEmploysRelData.salesOrg = ApplicationContext.get('user').getBoUserSales().getSalesOrg();
jsonBpaEmploysRelData.fromPKey = fromPKey;
jsonBpaEmploysRelData.toPKey = toPKey;

me.addItems([jsonBpaEmploysRelData]);
me.getItemByPKey(jsonBpaEmploysRelData.pKey).setObjectStatus(STATE.NEW | STATE.DIRTY);
