BusinessLogic.Code:
var liCustomerOpeningTime = me.getItemByPKey(pKey);

// Delete item if it is newly created (isNew=1)
if (Utils.isDefined(liCustomerOpeningTime) && liCustomerOpeningTime.getIsNew() == "1"){
  liCustomerOpeningTime.delete();
  me.setFilter("objectStatus", STATE.DIRTY | STATE.DELETED, "NCB");
}
