var promise = when.resolve();
var liCustomerOpeningTime = me.getItemByPKey(pKey);

if (Utils.isDefined(liCustomerOpeningTime)){
  liCustomerOpeningTime.delete();

  //save list explicitly after deleting opening time
  Facade.startTransaction();
  promise = Facade.saveListAsync(me).then(function(){
    return Facade.commitTransactionAsync();
  });
}
