BusinessLogic.Code:
var promise = Facade.getListAsync(LO_CUSTOMEROPENINGTIME, jsonParams).then(
  function(items) {
    if (Utils.isSfBackend()){
      items.forEach(function(item) {
        if (Utils.isEmptyString(item.timeFrom)) {
          item.timeFrom = "00:00";
        }

        if (Utils.isEmptyString(item.timeThru)) {
          item.timeThru = "00:00";
        }
      });
    }
    me.addItems(items, jsonParams);
    //Set days for which the Customer is open
    me.setOpenDays();
    //Reset dirty flag
    me.setObjectStatus(STATE.PERSISTED);
    return me;
  });
