BusinessLogic.Code:
var items = me.getItemsByParam({"name" : " "});

// Check if there are any customer with blank name
if(items.length > 0){
  var curItem = me.getCurrent();
  // For each customer delete it, if it is not the current item
  for(var i = 0; i < items.length; i++){
    if(curItem.getPKey() != items[i].getPKey() && Utils.isEmptyString(items[i].getName()) && items[i].getObjectStatus() == STATE.NEW){
      items[i].delete();
    }
  }
  //Filter out the items with deleted status
  me.setFilter("objectStatus", STATE.DIRTY | STATE.DELETED, "NCB");
}
