switch (sortBy) {
  case "Distance":
    this.orderBy({"distance" : "ASC", "name" : "ASC"});
    if(Utils.isDefined(this.getFirstItem()) && !isNaN(this.getFirstItem().getDistance())) {
      this.setCurrentByPKey(this.getFirstItemPKey());
    }
    break;    
  case "Alphabet":
    if(Utils.isEmptyString(CustomerPKey)){
    this.orderBy({"name" : "ASC"});
    this.setCurrentByPKey(this.getFirstItemPKey());
    }
    break;
}
