BusinessLogic.Code:
var promise = when.resolve();
var liCustomerPOSRelation = me.getItemByPKey(pKey);

if (Utils.isDefined(liCustomerPOSRelation)){
  liCustomerPOSRelation.setValidThru(Utils.addDays2AnsiFullDate(Utils.createDateToday(), -1));

  if (liCustomerPOSRelation.getValidThru() < liCustomerPOSRelation.getValidFrom()){
    liCustomerPOSRelation.setValidFrom(liCustomerPOSRelation.getValidThru());
  }

  liCustomerPOSRelation.setContinued("0");

  //save list explicitly after discontinuing pos
  Facade.startTransaction();
  promise = Facade.saveListAsync(me).then(function(){
    return Facade.commitTransactionAsync();
  });
}
