  function(items) {
    me.addItems(items, jsonParams);
    jsonParams.params.push({
      "field" : "asoName",
      "value" : "AsoOrdererOverview"
    });
    return me.addAsoInformation(jsonParams).then(
      function () {
        return me;
      });
  });
