var promise;

if (me.getClbStatus() === "Completed" && me.getOriginalClbStatus() !== "Completed") {
  promise = me.getBoJobManager().prepareSavePresettingJobs()
    .then(
    function () {
      return me.updateCustomerCallSetting().then(
        function () {
          return context;
        });
    });
} else {
  promise = when.resolve(context);
}
