BusinessLogic.Code:
var promise;

if (Utils.isEmptyString(this.getDateFrom()) || Utils.isEmptyString(this.getDateThru()) || Utils.isEmptyString(this.getTimeFrom()) || Utils.isEmptyString(this.getTimeThru()))
{
  var buttonValues = {};
  buttonValues[Localization.resolve("OK")] = "ok";
  promise = MessageBox.displayMessage(Localization.resolve("MessageBox_Title_Validation"), Localization.resolve("BoCall_validateCallDate"), buttonValues)
    .then(
    function(result)
    {
      return "invalid";    
    }
  );
}
else
{
  promise = when.resolve("valid");
}
