var promise;

if (!Utils.isEmptyString(lookup.getOpenDeliveries()) && lookup.getOpenDeliveries() != "0"){
  promise = when.resolve("1");
}
else if (!Utils.isEmptyString(lookup.getOpenOrders()) && lookup.getOpenOrders() != "0"){
  promise = when.resolve("2");
}
else{
  promise = when.resolve("0");
}
