if(!Utils.isPhone()){
  var chartHelper = me.getChartHelper();
  var chartString = "chart_" + containerName;
  var chartSelector = "#"+chartString;
  var d3Container = chartHelper.getUIContainer(containerName);
  d3Container.select(chartSelector).remove();
  d3Container.select("#colorContainer").remove();

  setTimeout(function (){
    me.initializePromotionStoreCalendarReport(promotionList, containerName, callDate);
  }, 100);
}
