BusinessLogic.Code:
//PKey replacement of historical questions is done during BoJobManager save
me.getLoQuestions().suspendListRefresh();
question.setClbMainPKey(me.getClbMainPKey());
question.setVisitDate(me.getReferenceDate());
question.setVisitTime(me.getTimeFrom());       
question.setObjectStatus(this.self.STATE_NEW_DIRTY);
me.getLoQuestions().resumeListRefresh(true);

if(question.getDone() !== "1"){
  question.setDone("1");
}
