BusinessLogic.Code:
if (Utils.isSfBackend()) {
  switch (loadType) {
    case "surveys":
      me.getLoPOS().setFilter("posId", " ");
      break;

    case "questions":
      if(me.considerPOSCheck == "0") me.getLoPOS().setFilter("posId", " ");
      else me.getLoPOS().resetFilter("posId");
      break;
  }
}
