BusinessLogic.Code:
	var me = this;
var acl = me.getACL();
acl.removeRight(AclObjectType.PROPERTY, "validFrom", AclPermission.EDIT);
acl.removeRight(AclObjectType.PROPERTY, "validThru", AclPermission.EDIT);

if(me.getMode() == "DragAndDrop") {	
	me.getLoTripList().getACL().removeRight(AclObjectType.OBJECT, "LoTripList", AclPermission.EDIT);
}
