var allday = me.getACL();
if (me.getCallBo().getAllDay() == "1"){
  allday.removeRight(AclObjectType.PROPERTY, "timeFrom", AclPermission.VISIBLE);                            
  allday.removeRight(AclObjectType.PROPERTY, "timeThru", AclPermission.VISIBLE);
  allday.removeRight(AclObjectType.PROPERTY, "duration", AclPermission.VISIBLE);
}
