BusinessLogic.Code:
var me = this;
var visible = false;

switch (token) {

  case "create":
    if(me.getMode() === "CreateTripList") {
      visible = true;
    }
    break;

  case "update":
    if(me.getMode() === "UpdateTripList") {
      visible = true;
    }
    break;

  case "delete":
    if(me.getMode() === "DeleteTripList") {
      visible = true;
    }
    break;
}
