BusinessLogic.Code:
var acl = this.getACL();
var type;

if(me.getMode() != "DeleteTripList") {
  if(Utils.isDefined(handlerParams)) {
    type = handlerParams.newValue;
  }
  if(type == "Weekly") {
    me.setDayOfWeek("0");
    acl.removeRight(AclObjectType.PROPERTY, "dayOfWeek", AclPermission.VISIBLE);
  }
  else {
    acl.addRight(AclObjectType.PROPERTY, "dayOfWeek", AclPermission.VISIBLE);
  }
  BindingUtils.refreshEARights();
}
