var displayTraffic = (option === "DisplayTraffic" && Framework.getProcessContext().displayTraffic !== "1");
var hideTraffic = (option === "HideTraffic" && Framework.getProcessContext().displayTraffic == "1");
var displayRoute = (option === "DisplayRoute" && Framework.getProcessContext().displayRoute !== "1");
var hideRoute = (option === "HideRoute" && Framework.getProcessContext().displayRoute == "1");
var result = displayTraffic || hideTraffic || displayRoute || hideRoute;
