var promise = BoFactory.createListAsync("LoCallAttachments")
  .then(function(imageListForSketching) {

    var latestPicture = me.getLatestLiAttachment();

    if (Utils.isDefined(latestPicture)) {
      imageListForSketching.addWeakReferencedItems([latestPicture]);
    }
    return imageListForSketching;
  });
