var visible = (me.getPhase() !== "Released") && (me.getPhase() !== "Canceled");
var runningTour = "";

if (Utils.isDefined(ApplicationContext.get('currentTourPKey'))){
  runningTour = ApplicationContext.get('currentTourPKey');
}

if(runningTour !== me.getTmgMainPKey()){
  visible = false;
}
