var idxAso = -1;
for (var index in context.jsonQuery.params)
{              
  if (context.jsonQuery.params[index].field === 'asoName') {
    idxAso = index;
  } 
}

if (idxAso > -1) {
  context.jsonQuery.params[idxAso].value = 'AsoCashOverview';
}

var promise = when.resolve(context);
