BusinessLogic.Code:
var loTactics = me.getLoPayTactics().getAllItems();
var amount = parseInt(me.getPaidAmount(), 10);

for(var idxTct = 0;idxTct<loTactics.length;idxTct++){
  var liTactic = loTactics[idxTct];

  if(Utils.isDefined(liTactic.getTacticApproved())){
    if(liTactic.getTacticApproved() == 'True'){
      amount += parseInt(liTactic.getActualCosts(), 10);
    }
  }
}
me.setPaidAmount(amount);
