var loTactics = me.getAllItems();

for(var i = 0; i < loTactics.length; i++) {
 var liTactic = loTactics[i];
  if(liTactic.getActualCosts() == liTactic.getRejectedAmount()) {
    liTactic.setTacticStatus("FullRejection");
  }
  else {
    liTactic.setTacticStatus("Approved");
  }
}
