if(Utils.isDefined(me.getLoPrmCttTactics())){
  var endDate = me.getDateThru();
  var idxProduct;
  var listPrmTacttics = me.getLoPrmCttTactics();
  var items = listPrmTacttics.getAllItems();

  for(idxProduct = 0; idxProduct < items.length; idxProduct++){
    var currentprmTacttic = items[idxProduct];
    currentprmTacttic.setDateThru(endDate);
  }
}

var promise = Facade.saveObjectAsync(me);
