var loTactic = me.getLoPrmCttTactics();
var liTactic = loTactic.getItemByPKey(pKey);

if(Utils.isDefined(liTactic)) {
  if(liTactic.getActive() == "1") {
    me.getLuPrmCttTacticProductCount().set('tacticCount', (me.getLuPrmCttTacticProductCount().getTacticCount() - 1));
  }
  liTactic.delete();
  loTactic.setFilter("objectStatus", STATE.DIRTY | STATE.DELETED, "NCB");
}
