BusinessLogic.Code:
var newError;
var me=this;    
var initiationDate = Utils.convertFullDate2Ansi(Utils.createDateToday());

// Validate if Due Date is blank
if (Utils.isDefined(me.getDueDate()))
{
  if((me.getDueDate() != Utils.getMinDate()) && (me.getDueDate() < initiationDate))
  {   
    newError = 
      {
      "level": "error", 
      "objectClass": "BoWizardCreateNewIssue", 
      "messageID": "CasBoIssueInitiationDateGTDueDate"
    };
    messageCollector.add(newError);
  }      
}
else
{
  me.setDueDate(Utils.getMinDate());
}
