var items = me.getAllItems();
var length = items.length;

for (var i = 0; i < length; i++){
  var callType = items[i].getCallType();
  var imageId = 'ClbTypeStatus_' + callType + '_Planned';
  items[i].setClbTypeStatus(imageId);
}

me.orderBy({ "clbText" : "ASC" });

