var myPageSize;

if (Utils.isDefined(Framework.getProcessContext().dailyReportList.getPageSize())) {
  myPageSize = Framework.getProcessContext().dailyReportList.getPageSize();
} else {
  myPageSize = 0;
}

var promise = Framework.getProcessContext().dailyReportList.loadCustomPageAsync(500 - myPageSize);
