BusinessLogic.Code:
var newParams;

if (Utils.isDefined(context.jsonQuery)) {
  newParams = context.jsonQuery;
  if (Utils.isOldParamsFormat(newParams)) {
    newParams = Utils.convertDsParamsOldToNew(newParams);
  }
}

//check if product list is defined
if(Utils.isDefined(newParams) && Utils.isDefined(newParams.prdMainPKeys)) {
  newParams.addCond_PrdMainPKeys = 'AND IvcMain.PrdMainPKey IN (' + newParams.prdMainPKeys +')';
}

context.jsonQuery = newParams;

var promise = when.resolve(context);
