BusinessLogic.Code:
var unitFactor = undefined;

var filter = {};
if (ivcMeasure == "QtyCUnit") 
{
  filter = {"isConsumerUnit": "1"};         
}
else if (ivcMeasure == "QtyOUnit")
{
  filter = { "isOrderUnit": "1" };         
}
else if (ivcMeasure == "QtyIUnit")
{
  filter = { "IsStocktakingUnit": "1" };         
}

var unitFactors = this.getItemsByParam(filter);
if (unitFactors.length > 0)
{
	unitFactor = unitFactors[0];
}
