BusinessLogic.Code:
var count = me.cardItemCount;
var numberVisibleNotifications = 5;

if(Utils.isPhone()) {
	numberVisibleNotifications = 3;
}

if(count <= numberVisibleNotifications){
  numberVisibleNotifications = count;
}

var info = " ";
if(numberVisibleNotifications != "0"){
	info = numberVisibleNotifications.toString() + " / " + count;
}
