BusinessLogic.Code:
var acl = me.getACL();

if(Utils.isDefined(handlerParams)) {
  if(handlerParams.newValue === "Current") {
    acl.addRight(AclObjectType.PROPERTY, "uoMScanDefaultUnit", AclPermission.VISIBLE);
  }
  else {
    acl.removeRight(AclObjectType.PROPERTY, "uoMScanDefaultUnit", AclPermission.VISIBLE);
  }
}
else if(me.getUoMScanBehavior() === "Current") {
  acl.addRight(AclObjectType.PROPERTY, "uoMScanDefaultUnit", AclPermission.VISIBLE);
}
else {
  acl.removeRight(AclObjectType.PROPERTY, "uoMScanDefaultUnit", AclPermission.VISIBLE);
}
BindingUtils.refreshEARights();
