BusinessLogic.Code:
var acl = me.getACL();

if((barcodeScanBehavior.isToggle && barcodeScanBehavior === "SelectIncrease") || barcodeScanBehavior === "SelectIncrease") {
  acl.addRight(AclObjectType.PROPERTY, "currentScanIncrementQuantity", AclPermission.VISIBLE);
}
else {
  acl.removeRight(AclObjectType.PROPERTY, "currentScanIncrementQuantity", AclPermission.VISIBLE);
}
BindingUtils.refreshEARights();
