var subItems;

var items = Utils.isDefined(availableUnits) ? availableUnits.getAllItems() : [];

subItems = new Array(items.length);
for(var i=0; i < items.length; i++) {
  subItems[i] = items[i].getUnitType();
}

var selectionBoxList = Utils.getToggleListObject("PrdLogisticUnit", subItems);
