BusinessLogic.Code:
var promise = when.resolve();
me.getLoItems().suspendListRefresh();

var fastOrderItems = fastOrderItems.getItems();

for(var i=0; i < fastOrderItems.length; i++) { 
  if(fastOrderItemDictionary.containsKey(fastOrderItems[i].pKey)){
    var currentItem = fastOrderItemDictionary.get(fastOrderItems[i].pKey);
    currentItem.setQuantity(parseInt(fastOrderItems[i].quantity, 10));
  }
}    

promise = me.getLoItems().resumeListRefreshAsync(false,true);
