BusinessLogic.Code:
var me = this;
var itemQuantityEdited;
var editedItems = me.getLoItems().getItemsByParamArray([ 
  {"promotionPKey" : me.getSelectedPromotionPKey(), "op" : "EQ"}, 
  {"edited" : "1", "op" : "EQ"},
  {"rewardPKey" : " " , "op" : "EQ"}, // no free items from rewards
  {"sdoItemMetaPKey" : me.boOrderMeta.luOrderMetaForFreeItems.sdoItemMetaPKey , "op" : "NE"}]); // no free items

if(editedItems.length > 0){
	itemQuantityEdited = "Yes";
}
else{
	itemQuantityEdited = "No";
}
