BusinessLogic.Code:
var me = this;

if(me.getSplittingOption() == "1")
{ 
  if(bpaSplittingOption === "Yes")
  {
    me.setSplittingRule(bpaSplittingRule);
  }
  else if(bpaSplittingOption === "FromSalesDoc" && me.getBoOrderMeta().getSplittingOption() === "Yes")
  {
    me.setSplittingRule(me.getBoOrderMeta().getSplittingRule());
  }
  else
  {
    me.setSplittingRule(' ');
  }
}
else
{
  me.setSplittingRule(' ');
}
