BusinessLogic.Code:
var prdType = mainItem.getPrdType();
var movDir = mainItem.getMovementDirection();

if(movDir === "Out" && prdType !== "Empty")
{
  mainItem.setSplittingGroup(Localization.resolve("Order_SplittingGroup_FullGoodsDelivery"));
}
else if(movDir === "Out" && prdType === "Empty")
{
  mainItem.setSplittingGroup(Localization.resolve("Order_SplittingGroup_EmptiesDelivery"));
}
else if(movDir === "In" && prdType !== "Empty")
{
  mainItem.setSplittingGroup(Localization.resolve("Order_SplittingGroup_FullGoodsReturn"));
}
else if(movDir === "In" && prdType === "Empty")
{
  mainItem.setSplittingGroup(Localization.resolve("Order_SplittingGroup_EmptiesReturn"));
}
else
{
  mainItem.setSplittingGroup(Localization.resolve("Order_SplittingGroup_Misc"));
}
