var result = false;

if ((this.getBoOrderMeta().getItemListOption() == "Hierarchy")  && (this.getBoOrderMeta().getMobilityRelevant() == "1")) {
    if ((this.getPhase() != "Released") && (this.getPhase() != "Closed") && (this.getPhase() != "Canceled") && (this.getPhase() != "Ready") && (this.getPhase() != "Feedback")) {            
        result = true;
    }
}
